import { useRef, useState, useEffect } from "react";
import axios from "axios";
import '../styles/mobile.css';

export default function CapturePage() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [steps, setSteps] = useState(0);
  const [countdown, setCountdown] = useState(null);
  const [settings, setSettings] = useState({
    eventName: '',
    background: { type: 'color', value: '#ff0000' },
    logo: null
  });
  const [notification, setNotification] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fallback API URL if environment variable is not set
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000';

  // Load settings from localStorage
  useEffect(() => {
    const savedSettings = localStorage.getItem('photoBoothSettings');
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  }, []);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) videoRef.current.srcObject = stream;
      })
      .catch((error) => {
        console.warn("Camera access denied:", error);
        setNotification({
          type: "error",
          message: "Camera access required. Please allow webcam access and refresh the page."
        });
      });
  }, []);

  const startCapture = () => {
    if (steps >= 3) return;
    setCountdown(3);
    const countdownInterval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          takePhoto();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const takePhoto = () => {
    const ctx = canvasRef.current.getContext("2d");
    const width = 480;
    const height = 240;

    if (steps === 0) {
      // Set background
      if (settings.background.type === "color") {
        ctx.fillStyle = settings.background.value;
        ctx.fillRect(0, 0, width, 720);
      }
    }

    ctx.drawImage(videoRef.current, 0, steps * height, width, height);

    // Optional logo overlay
    if (settings.logo) {
      const img = new Image();
      img.src = settings.logo; // settings.logo is already a base64 string
      img.onload = () => {
        ctx.drawImage(img, 10, 10, 100, 50);
      };
    }

    // Optional event name text on last step
    if (steps === 2 && settings.eventName) {
      ctx.fillStyle = "#ffffff";
      ctx.font = "24px sans-serif";
      ctx.fillText(settings.eventName, 10, 710);
    }

    setSteps(steps + 1);
  };

  const submit = async () => {
    if (isSubmitting) return;

    try {
      setIsSubmitting(true);
      setNotification(null);

      const dataUrl = canvasRef.current.toDataURL("image/jpeg", 0.8);

      await axios.post(`${API_BASE_URL}/api/strips`, {
        image: dataUrl,
        eventName: settings.eventName,
        background: settings.background,
        logo: settings.logo,
      });

      setNotification({
        type: "success",
        message: "✅ Thank you! Your photo strip has been sent for print."
      });

      setSteps(0);
      canvasRef.current.getContext("2d").clearRect(0, 0, 480, 720);
    } catch (error) {
      console.warn("Upload failed:", error);
      setNotification({
        type: "error",
        message: "❌ Failed to upload. Please check your connection and try again."
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Auto-hide notification after 5 seconds
  useEffect(() => {
    if (notification) {
      const timer = setTimeout(() => {
        setNotification(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [notification]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-teal-500">
      {/* Notification */}
      {notification && (
        <div className={`fixed top-4 left-1/2 transform -translate-x-1/2 z-50 px-6 py-3 rounded-lg shadow-lg ${
          notification.type === 'success'
            ? 'bg-green-500 text-white'
            : 'bg-red-500 text-white'
        }`}>
          <div className="flex items-center space-x-2">
            <span>{notification.message}</span>
            <button
              onClick={() => setNotification(null)}
              className="ml-2 text-white hover:text-gray-200"
            >
              ✕
            </button>
          </div>
        </div>
      )}
      <div className="container mx-auto px-2 sm:px-4 lg:px-6 py-3 sm:py-4 md:py-6 lg:py-8">
        {/* Header */}
        <div className="text-center mb-4 sm:mb-6 md:mb-8 lg:mb-12">
          <h1 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white mb-1 sm:mb-2 drop-shadow-lg">
            📸 Strip Photobooth
          </h1>
          <p className="text-sm sm:text-base md:text-lg lg:text-xl text-white/80 px-2 sm:px-4">Create amazing photo strips instantly!</p>
        </div>

        {/* Single Column Layout - Camera and Submit */}
        <div className="max-w-sm sm:max-w-md md:max-w-lg mx-auto px-2 sm:px-0">
          <div className="bg-white/10 backdrop-blur-lg rounded-xl sm:rounded-2xl p-3 sm:p-4 md:p-6 border border-white/20 shadow-2xl">
            <h3 className="text-lg sm:text-xl md:text-2xl font-bold text-white mb-3 sm:mb-4 text-center">📹 Camera</h3>

            <div className="relative">
              <video
                ref={videoRef}
                autoPlay
                playsInline
                className="w-full aspect-video rounded-lg sm:rounded-xl shadow-xl border-2 sm:border-3 md:border-4 border-white/30"
              />

              {countdown && (
                <div className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-lg sm:rounded-xl">
                  <div className="text-3xl sm:text-5xl md:text-6xl lg:text-7xl font-bold text-white animate-pulse drop-shadow-lg">
                    {countdown}
                  </div>
                </div>
              )}

              {/* Progress indicator */}
              <div className="mt-2 sm:mt-3 md:mt-4 flex justify-center space-x-1.5 sm:space-x-2">
                {[1, 2, 3].map((step) => (
                  <div
                    key={step}
                    className={`w-2.5 h-2.5 sm:w-3 sm:h-3 md:w-4 md:h-4 rounded-full ${
                      steps >= step ? 'bg-green-400' : 'bg-white/30'
                    } transition-colors duration-300`}
                  />
                ))}
              </div>
            </div>

            {/* Capture Button */}
            <button
              onClick={startCapture}
              disabled={steps >= 3}
              className={`mt-3 sm:mt-4 md:mt-6 w-full py-3 sm:py-4 md:py-5 px-4 sm:px-6 rounded-lg sm:rounded-xl font-bold text-sm sm:text-base md:text-lg transition-all duration-300 touch-manipulation ${
                steps >= 3
                  ? 'bg-gray-400 cursor-not-allowed text-gray-200'
                  : 'bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 active:from-pink-700 active:to-red-700 text-white shadow-lg hover:shadow-xl transform hover:scale-105 active:scale-95'
              }`}
            >
              <span className="block xs:hidden">{steps >= 3 ? '✅ Done!' : `📸 ${steps + 1}`}</span>
              <span className="hidden xs:block sm:hidden">{steps >= 3 ? '✅ Done!' : `📸 Photo ${steps + 1}`}</span>
              <span className="hidden sm:block">{steps >= 3 ? '✅ All Photos Captured!' : `📸 Capture Photo ${steps + 1}`}</span>
            </button>

            {/* Submit Section - Directly below Capture Button */}
            <div className="mt-3 sm:mt-4 md:mt-6 pt-3 sm:pt-4 md:pt-6 border-t border-white/20">
              <div className="text-center mb-2 sm:mb-3 md:mb-4">
                <p className="text-white/80 text-xs sm:text-sm md:text-base leading-relaxed">
                  {steps >= 3 ? 'Ready to submit your photo strip!' : `Capture ${3 - steps} more photo${3 - steps > 1 ? 's' : ''} to submit`}
                </p>
                {settings.eventName && (
                  <p className="text-blue-300 text-xs sm:text-sm mt-1">🎉 Event: {settings.eventName}</p>
                )}
              </div>

              {/* Submit Button */}
              <button
                onClick={submit}
                disabled={steps < 3 || isSubmitting}
                className={`w-full py-3 sm:py-4 md:py-5 px-4 sm:px-6 rounded-lg sm:rounded-xl font-bold text-sm sm:text-base md:text-lg transition-all duration-300 touch-manipulation ${
                  steps >= 3 && !isSubmitting
                    ? 'bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 active:from-green-700 active:to-emerald-700 text-white shadow-lg hover:shadow-xl transform hover:scale-105 active:scale-95'
                    : 'bg-gray-600 text-gray-300 cursor-not-allowed'
                }`}
              >
                {isSubmitting ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-3 w-3 sm:h-4 sm:w-4 md:h-5 md:w-5 border-b-2 border-white mr-2"></div>
                    <span className="text-xs sm:text-sm md:text-base">Submitting...</span>
                  </div>
                ) : (
                  <>
                    <span className="block xs:hidden">📤 Submit</span>
                    <span className="hidden xs:block sm:hidden">📤 Submit Strip</span>
                    <span className="hidden sm:block">📤 Submit Strip</span>
                  </>
                )}
              </button>
            </div>
          </div>

          {/* Preview Canvas (hidden) */}
          <canvas
            ref={canvasRef}
            width="480"
            height="720"
            className="hidden"
          />
        </div>
      </div>
    </div>
  );
}
