import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [auth, setAuth] = useState(false);
  const [strips, setStrips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Debug: Log environment variables
  console.log('Environment variables:', {
    REACT_APP_API_BASE_URL: process.env.REACT_APP_API_BASE_URL,
    NODE_ENV: process.env.NODE_ENV
  });

  // Fallback API URL if environment variable is not set
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000';

  const login = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Attempting login...');
      console.log('API URL:', process.env.REACT_APP_API_BASE_URL);

      const res = await axios.post(`${API_BASE_URL}/api/auth/login`, {
        username: "admin",
        password: "secret"
      });

      console.log('Login response:', res.data);
      if (res.data.success) {
        setAuth(true);
        console.log('Login successful, auth set to true');
      } else {
        setError('Login failed: Invalid credentials');
      }
    } catch (error) {
      console.error('Login failed:', error);
      setError(`Login failed: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const load = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Loading strips...');
      console.log('API URL:', process.env.REACT_APP_API_BASE_URL);

      const res = await axios.get(`${API_BASE_URL}/api/strips`);
      console.log('Strips loaded:', res.data);
      console.log('Number of strips:', res.data.length);

      setStrips(res.data);
    } catch (error) {
      console.error('Failed to load strips:', error);
      setError(`Failed to load strips: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const mark = async (id) => {
    try {
      await axios.patch(`${API_BASE_URL}/api/strips/${id}/mark-printed`);
      load();
    } catch (error) {
      console.error('Failed to mark strip as printed:', error);
    }
  };

  const del = async (id) => {
    try {
      await axios.delete(`${API_BASE_URL}/api/strips/${id}`);
      load();
    } catch (error) {
      console.error('Failed to delete strip:', error);
    }
  };

  const testAPI = async () => {
    try {
      console.log('Testing API directly...');
      const response = await fetch('http://localhost:5000/api/strips');
      const data = await response.json();
      console.log('Direct fetch result:', data);
      alert(`Direct API test: Found ${data.length} strips`);
    } catch (error) {
      console.error('Direct API test failed:', error);
      alert('Direct API test failed: ' + error.message);
    }
  };

  useEffect(()=>{
    console.log('Auth state changed:', auth);
    if(auth) load();
  }, [auth]);

  // Test API connection on component mount
  useEffect(() => {
    console.log('AdminDashboard component mounted');
    console.log('Testing API connection...');

    // Test basic connectivity
    fetch(`${API_BASE_URL}/api/strips`)
      .then(response => {
        console.log('API connection test - Response status:', response.status);
        return response.json();
      })
      .then(data => {
        console.log('API connection test - Success:', data.length, 'strips found');
      })
      .catch(error => {
        console.error('API connection test - Failed:', error);
      });
  }, []);

  console.log('Rendering AdminDashboard, auth:', auth, 'strips:', strips.length);

  if (!auth) return (
    <div className="p-4">
      <h2 className="text-xl mb-4">🛠 Admin Dashboard</h2>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
          {error}
        </div>
      )}

      <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded">
        <h3 className="font-bold mb-2">Debug Info (Before Login):</h3>
        <p>API Base URL (env): {process.env.REACT_APP_API_BASE_URL || 'UNDEFINED'}</p>
        <p>API Base URL (used): {API_BASE_URL}</p>
        <p>Loading: {loading.toString()}</p>
      </div>

      <button
        onClick={login}
        disabled={loading}
        className={`px-4 py-2 rounded text-white ${loading ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600'}`}
      >
        {loading ? 'Logging in...' : 'Admin Login'}
      </button>
    </div>
  );

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl">🛠 Admin Dashboard</h2>
        <button onClick={() => setAuth(false)} className="bg-red-500 text-white px-3 py-1 rounded text-sm">Logout</button>
      </div>
      <p className="mb-4">Authenticated: {auth ? 'Yes' : 'No'} | Strips count: {strips.length} | Loading: {loading.toString()}</p>

      {error && (
        <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
          <strong>Error:</strong> {error}
        </div>
      )}

      <div className="mb-4 p-3 bg-gray-100 rounded">
        <h3 className="font-bold mb-2">Debug Info:</h3>
        <p>API Base URL (env): {process.env.REACT_APP_API_BASE_URL || 'UNDEFINED'}</p>
        <p>API Base URL (used): {API_BASE_URL}</p>
        <p>Auth State: {auth.toString()}</p>
        <p>Loading: {loading.toString()}</p>
        <p>Error: {error || 'None'}</p>
        <p>Strips Array Length: {strips.length}</p>
        <details className="mt-2">
          <summary className="cursor-pointer font-semibold">Strips Data (click to expand)</summary>
          <pre className="mt-2 text-xs bg-white p-2 rounded overflow-auto max-h-40">
            {JSON.stringify(strips, null, 2)}
          </pre>
        </details>
        <div className="mt-2 space-x-2">
          <button onClick={testAPI} className="bg-purple-500 text-white px-3 py-1 rounded text-sm">Test API Direct</button>
          <button onClick={load} disabled={loading} className={`px-3 py-1 rounded text-sm text-white ${loading ? 'bg-gray-400' : 'bg-orange-500'}`}>
            {loading ? 'Loading...' : 'Load Strips'}
          </button>
        </div>
      </div>

      {strips.length === 0 ? (
        <div className="text-center py-8">
          <p className="text-gray-500">No strips found. Try capturing some photos first!</p>
          <div className="mt-4 space-x-2">
            <button onClick={load} className="bg-blue-500 text-white px-4 py-2 rounded">Refresh</button>
            <button onClick={() => window.open('http://localhost:3000')} className="bg-green-500 text-white px-4 py-2 rounded">Go to Camera</button>
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {strips.map(s=>(
            <div key={s._id} className="border p-2">
              <img src={s.imageUrl} alt="" className="w-full" onError={(e) => {
                console.error('Image failed to load:', s.imageUrl);
                e.target.style.display = 'none';
              }}/>
              <p className="text-sm">{new Date(s.timestamp).toLocaleString()}</p>
              <p className="font-bold">{s.eventName || 'No event name'}</p>
              <p>Printed: {s.printed ? "✅" : "❌"}</p>
              <div className="flex flex-col gap-1 mt-2">
                <button onClick={()=>mark(s._id)} className="bg-yellow-400 px-2 py-1 text-sm">Mark Printed</button>
                <button onClick={()=>del(s._id)} className="bg-red-500 px-2 py-1 text-white text-sm">Delete</button>
                <button onClick={()=>window.open(s.imageUrl)} className="bg-blue-500 px-2 py-1 text-white text-sm">Download</button>
                <button onClick={()=>window.print()} className="bg-green-600 px-2 py-1 text-white text-sm">Print</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
