import { useRef, useState, useEffect } from "react";
import axios from "axios";

export default function CapturePage() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [steps, setSteps] = useState(0);
  const [countdown, setCountdown] = useState(null);
  const [background, setBackground] = useState({ type: "color", value: "#ff0000" });
  const [eventName, setEventName] = useState("");
  const [logo, setLogo] = useState(null);
  const [notification, setNotification] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) videoRef.current.srcObject = stream;
      })
      .catch((error) => {
        console.warn("Camera access denied:", error);
        setNotification({
          type: "error",
          message: "Camera access required. Please allow webcam access and refresh the page."
        });
      });
  }, []);

  const startCapture = () => {
    if (steps >= 3) return;
    setCountdown(3);
    const countdownInterval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          takePhoto();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const takePhoto = () => {
    const ctx = canvasRef.current.getContext("2d");
    const width = 480;
    const height = 240;

    if (steps === 0) {
      // Set background
      if (background.type === "color") {
        ctx.fillStyle = background.value;
        ctx.fillRect(0, 0, width, 720);
      }
    }

    ctx.drawImage(videoRef.current, 0, steps * height, width, height);

    // Optional logo overlay
    if (logo) {
      const img = new Image();
      img.src = URL.createObjectURL(logo);
      img.onload = () => {
        ctx.drawImage(img, 10, 10, 100, 50);
      };
    }

    // Optional event name text on last step
    if (steps === 2) {
      ctx.fillStyle = "#ffffff";
      ctx.font = "24px sans-serif";
      ctx.fillText(eventName, 10, 710);
    }

    setSteps(steps + 1);
  };

  const convertLogoToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(file);
    });
  };

  const submit = async () => {
    if (isSubmitting) return;

    try {
      setIsSubmitting(true);
      setNotification(null);

      const dataUrl = canvasRef.current.toDataURL("image/jpeg", 0.8);

      let logoBase64 = "";
      if (logo) {
        logoBase64 = await convertLogoToBase64(logo);
      }

      await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/strips`, {
        image: dataUrl,
        eventName,
        background,
        logo: logoBase64,
      });

      setNotification({
        type: "success",
        message: "✅ Thank you! Your photo strip has been sent for print."
      });

      setSteps(0);
      canvasRef.current.getContext("2d").clearRect(0, 0, 480, 720);
      setEventName("");
      setLogo(null);
    } catch (error) {
      console.warn("Upload failed:", error);
      setNotification({
        type: "error",
        message: "❌ Failed to upload. Please check your connection and try again."
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Auto-hide notification after 5 seconds
  useEffect(() => {
    if (notification) {
      const timer = setTimeout(() => {
        setNotification(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [notification]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-teal-500">
      {/* Notification */}
      {notification && (
        <div className={`fixed top-4 left-1/2 transform -translate-x-1/2 z-50 px-6 py-3 rounded-lg shadow-lg ${
          notification.type === 'success'
            ? 'bg-green-500 text-white'
            : 'bg-red-500 text-white'
        }`}>
          <div className="flex items-center space-x-2">
            <span>{notification.message}</span>
            <button
              onClick={() => setNotification(null)}
              className="ml-2 text-white hover:text-gray-200"
            >
              ✕
            </button>
          </div>
        </div>
      )}
      <div className="container mx-auto px-3 sm:px-4 lg:px-6 py-4 sm:py-6 lg:py-8">
        {/* Header */}
        <div className="text-center mb-6 sm:mb-8 lg:mb-12">
          <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-2 drop-shadow-lg">
            📸 Strip Photobooth
          </h1>
          <p className="text-base sm:text-lg md:text-xl text-white/80 px-4">Create amazing photo strips instantly!</p>
        </div>

        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6 lg:gap-8">
            {/* Camera Section */}
            <div className="bg-white/10 backdrop-blur-lg rounded-xl sm:rounded-2xl p-4 sm:p-6 border border-white/20 order-1 lg:order-1">
              <h3 className="text-xl sm:text-2xl font-bold text-white mb-3 sm:mb-4 text-center">📹 Camera</h3>

              <div className="relative">
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  className="w-full aspect-video sm:aspect-auto rounded-lg sm:rounded-xl shadow-2xl border-2 sm:border-4 border-white/30"
                />

                {countdown && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-lg sm:rounded-xl">
                    <div className="text-4xl sm:text-6xl md:text-7xl lg:text-8xl font-bold text-white animate-pulse drop-shadow-lg">
                      {countdown}
                    </div>
                  </div>
                )}

                {/* Progress indicator */}
                <div className="mt-3 sm:mt-4 flex justify-center space-x-2">
                  {[1, 2, 3].map((step) => (
                    <div
                      key={step}
                      className={`w-3 h-3 sm:w-4 sm:h-4 rounded-full ${
                        steps >= step ? 'bg-green-400' : 'bg-white/30'
                      } transition-colors duration-300`}
                    />
                  ))}
                </div>
              </div>

              <button
                onClick={startCapture}
                disabled={steps >= 3}
                className={`mt-4 sm:mt-6 w-full py-3 sm:py-4 px-4 sm:px-6 rounded-lg sm:rounded-xl font-bold text-base sm:text-lg transition-all duration-300 ${
                  steps >= 3
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 text-white shadow-lg hover:shadow-xl transform hover:scale-105 active:scale-95'
                }`}
              >
                <span className="block sm:hidden">{steps >= 3 ? '✅ Done!' : `📸 Photo ${steps + 1}`}</span>
                <span className="hidden sm:block">{steps >= 3 ? '✅ All Photos Captured!' : `📸 Capture Photo ${steps + 1}`}</span>
              </button>
            </div>

            {/* Settings Section */}
            <div className="bg-white/10 backdrop-blur-lg rounded-xl sm:rounded-2xl p-4 sm:p-6 border border-white/20 order-2 lg:order-2">
              <h3 className="text-xl sm:text-2xl font-bold text-white mb-3 sm:mb-4 text-center">⚙️ Settings</h3>

              <div className="space-y-4 sm:space-y-6">
                {/* Event Name */}
                <div>
                  <label className="block text-white font-semibold mb-2 text-sm sm:text-base">🎉 Event Name</label>
                  <input
                    type="text"
                    placeholder="Enter event name..."
                    value={eventName}
                    onChange={(e) => setEventName(e.target.value)}
                    className="w-full p-2.5 sm:p-3 rounded-lg sm:rounded-xl border-2 border-white/30 bg-white/20 text-white placeholder-white/60 focus:border-white focus:outline-none transition-colors text-sm sm:text-base"
                  />
                </div>

                {/* Background Color */}
                <div>
                  <label className="block text-white font-semibold mb-2 text-sm sm:text-base">🎨 Background Color</label>
                  <div className="flex items-center space-x-2 sm:space-x-3">
                    <input
                      type="color"
                      value={background.value}
                      onChange={(e) => setBackground({ type: "color", value: e.target.value })}
                      className="w-12 h-10 sm:w-16 sm:h-12 rounded-lg border-2 border-white/30 cursor-pointer"
                    />
                    <span className="text-white font-mono bg-white/20 px-2 py-1.5 sm:px-3 sm:py-2 rounded-lg text-xs sm:text-sm flex-1 text-center">
                      {background.value}
                    </span>
                  </div>
                </div>

                {/* Logo Upload */}
                <div>
                  <label className="block text-white font-semibold mb-2 text-sm sm:text-base">🖼️ Logo (Optional)</label>
                  <div className="relative">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setLogo(e.target.files[0])}
                      className="w-full p-2.5 sm:p-3 rounded-lg sm:rounded-xl border-2 border-white/30 bg-white/20 text-white text-xs sm:text-sm file:mr-2 sm:file:mr-4 file:py-1.5 sm:file:py-2 file:px-2 sm:file:px-4 file:rounded-lg file:border-0 file:bg-white/30 file:text-white hover:file:bg-white/40 transition-colors file:text-xs sm:file:text-sm"
                    />
                  </div>
                  {logo && (
                    <p className="text-green-300 text-xs sm:text-sm mt-2">✅ Logo selected: {logo.name}</p>
                  )}
                </div>
              </div>

              {/* Submit Button */}
              {steps === 3 && (
                <button
                  onClick={submit}
                  disabled={isSubmitting}
                  className={`mt-4 sm:mt-6 w-full py-3 sm:py-4 px-4 sm:px-6 font-bold text-base sm:text-lg rounded-lg sm:rounded-xl shadow-lg transition-all duration-300 ${
                    isSubmitting
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white hover:shadow-xl transform hover:scale-105 active:scale-95'
                  }`}
                >
                  {isSubmitting ? (
                    <>
                      <span className="block sm:hidden">⏳ Uploading...</span>
                      <span className="hidden sm:block">⏳ Uploading Strip...</span>
                    </>
                  ) : (
                    <>
                      <span className="block sm:hidden">🚀 Submit</span>
                      <span className="hidden sm:block">🚀 Submit Strip</span>
                    </>
                  )}
                </button>
              )}
            </div>
          </div>

          {/* Preview Canvas (hidden) */}
          <canvas
            ref={canvasRef}
            width="480"
            height="720"
            className="hidden"
          />
        </div>
      </div>
    </div>
  );
}
