import { useEffect } from 'react';
import '../print.css';

const PrintStrip = ({ strip, onClose }) => {
  useEffect(() => {
    // Auto-trigger print dialog when component mounts
    const timer = setTimeout(() => {
      window.print();
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Listen for print events
    const handleAfterPrint = () => {
      onClose();
    };

    window.addEventListener('afterprint', handleAfterPrint);
    return () => window.removeEventListener('afterprint', handleAfterPrint);
  }, [onClose]);

  if (!strip) return null;

  return (
    <div className="print-container">

      {/* Print Layout */}
      <div className="strip-print-layout">
        <div className="strip-grid">
          {/* Generate 12 copies of the photo (2x6 grid) */}
          {Array.from({ length: 12 }, (_, index) => (
            <div
              key={index}
              className="strip-photo"
              style={{
                backgroundImage: `url(${strip.imageUrl})`,
                backgroundColor: strip.customBackground?.value || '#ffffff',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat'
              }}
            />
          ))}

          {/* Strip Information */}
          <div className="strip-info">
            <div style={{ fontWeight: 'bold', marginBottom: '2px' }}>
              {strip.eventName || 'Photo Strip'}
            </div>
            <div style={{ fontSize: '6pt', opacity: 0.8 }}>
              {new Date(strip.timestamp).toLocaleDateString()} ‚Ä¢ {new Date(strip.timestamp).toLocaleTimeString()}
            </div>
          </div>
        </div>
      </div>

      {/* Screen Preview and Controls */}
      <div className="no-print print-preview">
        <div className="text-center mb-4">
          <h3 className="text-lg font-bold mb-2">Print Preview - 2x6 Strip Format</h3>
          <p className="text-sm text-gray-600 mb-4">
            {strip.eventName || 'Photo Strip'} - {new Date(strip.timestamp).toLocaleDateString()}
          </p>
        </div>

        <div className="strip-grid mb-4">
          {Array.from({ length: 12 }, (_, index) => (
            <div
              key={index}
              className="strip-photo"
              style={{
                backgroundImage: `url(${strip.imageUrl})`,
                backgroundColor: strip.customBackground?.value || '#ffffff'
              }}
            />
          ))}
        </div>

        <div className="flex justify-center space-x-4">
          <button
            onClick={() => window.print()}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            üñ®Ô∏è Print
          </button>
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
          >
            ‚ùå Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default PrintStrip;
