import { useEffect, useState, useRef } from 'react';
import '../print.css';

const PrintStrip = ({ strip, onClose }) => {
  const [individualPhotos, setIndividualPhotos] = useState([]);
  const canvasRef = useRef(null);

  useEffect(() => {
    // Extract individual photos from the combined image
    if (strip?.imageUrl) {
      extractIndividualPhotos();
    }
  }, [strip?.imageUrl]);

  const extractIndividualPhotos = () => {
    const img = new Image();
    img.crossOrigin = 'anonymous';
    img.onload = () => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // Original image dimensions: 480x720 (3 photos of 480x240 each)
      const photoWidth = 480;
      const photoHeight = 240;

      canvas.width = photoWidth;
      canvas.height = photoHeight;

      const photos = [];

      // Extract each photo section
      for (let i = 0; i < 3; i++) {
        // Clear canvas
        ctx.clearRect(0, 0, photoWidth, photoHeight);

        // Draw the specific photo section
        ctx.drawImage(
          img,
          0, i * photoHeight, photoWidth, photoHeight, // Source: x, y, width, height
          0, 0, photoWidth, photoHeight // Destination: x, y, width, height
        );

        // Convert to data URL
        photos.push(canvas.toDataURL('image/jpeg', 0.9));
      }

      setIndividualPhotos(photos);
    };
    img.src = strip.imageUrl;
  };

  useEffect(() => {
    // Auto-trigger print dialog when component mounts
    const timer = setTimeout(() => {
      window.print();
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Listen for print events
    const handleAfterPrint = () => {
      onClose();
    };

    window.addEventListener('afterprint', handleAfterPrint);
    return () => window.removeEventListener('afterprint', handleAfterPrint);
  }, [onClose]);

  if (!strip) return null;

  return (
    <div className="print-container">

      {/* Print Layout */}
      <div className="strip-print-layout">
        {/* Single Strip for 2x6 inch GSM paper */}
        <div className="photo-strip-container">
          <div className="single-photo-strip">
            {/* 3 different photos vertically - each showing different captured photo */}
            {Array.from({ length: 3 }, (_, index) => {
              const photoUrl = individualPhotos[index] || strip.imageUrl;

              return (
                <div
                  key={`photo-${index}`}
                  className="strip-photo-frame"
                  style={{
                    backgroundImage: individualPhotos[index] ? `url(${individualPhotos[index]})` : `url(${strip.imageUrl})`,
                    backgroundColor: strip.customBackground?.value || '#ff0000',
                    backgroundSize: individualPhotos[index] ? 'cover' : '100% 300%',
                    backgroundPosition: individualPhotos[index] ? 'center' : `center ${index * -100}%`,
                    backgroundRepeat: 'no-repeat'
                  }}
                />
              );
            })}

            {/* Text section */}
            <div className="strip-text-section">
              <div className="strip-event-name">{strip.eventName || 'Event Name'}</div>
              <div className="strip-date">{new Date(strip.timestamp).toLocaleDateString()}</div>
            </div>
          </div>
        </div>
      </div>

      {/* Screen Preview and Controls */}
      <div className="no-print print-preview">
        <div className="text-center mb-6">
          <h3 className="text-xl font-bold mb-2 text-gray-800">Print Preview - Single Strip (2x6 inch GSM)</h3>
          <p className="text-sm text-gray-600 mb-2">
            <strong>{strip.eventName || 'Photo Strip'}</strong>
          </p>
          <p className="text-xs text-gray-500 mb-4">
            {new Date(strip.timestamp).toLocaleDateString()} • {new Date(strip.timestamp).toLocaleTimeString()}
          </p>
          <div className="text-xs text-blue-600 bg-blue-50 p-2 rounded">
            📄 Single strip for 2x6 inch GSM paper • 3 different photos in 3 boxes
          </div>
          <div className="text-xs text-green-600 bg-green-50 p-2 rounded mt-2">
            ✅ Each box shows a different captured photo • Perfect for photo booth strips
          </div>
        </div>

        <div className="preview-container mb-6">
          {/* Single Strip Preview for 2x6 inch paper */}
          <div className="preview-single-strip">
            {Array.from({ length: 3 }, (_, index) => {
              // Each photo is 1/3 of the total image height
              // Photo 1: top third (0% to 33.33%)
              // Photo 2: middle third (33.33% to 66.66%)
              // Photo 3: bottom third (66.66% to 100%)
              const backgroundPositionY = index * -100; // -0%, -100%, -200%

              return (
                <div
                  key={`preview-${index}`}
                  className="preview-photo-frame"
                  style={{
                    backgroundImage: `url(${strip.imageUrl})`,
                    backgroundColor: strip.customBackground?.value || '#ff0000',
                    backgroundSize: '100% 300%', // Make image 3x taller to crop sections
                    backgroundPosition: `center ${backgroundPositionY}%`,
                    backgroundRepeat: 'no-repeat'
                  }}
                />
              );
            })}

            <div className="preview-text-section">
              <div className="preview-event-name">{strip.eventName || 'Event Name'}</div>
              <div className="preview-date">{new Date(strip.timestamp).toLocaleDateString()}</div>
            </div>
          </div>
        </div>

        <div className="flex justify-center space-x-4">
          <button
            onClick={() => {
              // Small delay to ensure styles are applied
              setTimeout(() => {
                window.print();
              }, 100);
            }}
            className="px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 font-semibold shadow-lg transition-all duration-300 transform hover:scale-105"
          >
            🖨️ Print Strip
          </button>
          <button
            onClick={onClose}
            className="px-6 py-3 bg-gradient-to-r from-gray-500 to-gray-600 text-white rounded-lg hover:from-gray-600 hover:to-gray-700 font-semibold shadow-lg transition-all duration-300 transform hover:scale-105"
          >
            ❌ Cancel
          </button>
        </div>

        <div className="mt-6 p-4 bg-green-50 rounded-lg border border-green-200">
          <h4 className="font-bold text-green-800 mb-2">📋 2x6 Inch GSM Paper Print Instructions:</h4>
          <div className="text-xs text-green-700 space-y-1">
            <p>• <strong>Paper Size:</strong> 2x6 inch GSM photo paper (portrait orientation)</p>
            <p>• <strong>Format:</strong> Single strip with 3 different photos + elegant text</p>
            <p>• <strong>Margins:</strong> 0.1" top/bottom, 0.15" left/right for perfect fit</p>
            <p>• <strong>Scale:</strong> Set printer to "Actual Size" or "100%" (NOT "Fit to Page")</p>
            <p>• <strong>Quality:</strong> Select "High" or "Photo" quality setting</p>
            <p>• <strong>Custom Size:</strong> Set paper size to 2" x 6" in printer settings</p>
          </div>
          <div className="mt-2 text-xs text-green-600 font-medium">
            ✅ Each box shows different captured photo • One strip per page • Red background preserved
          </div>
        </div>
      </div>
    </div>
  );
};

export default PrintStrip;
