import { useRef, useState, useEffect } from "react";
import axios from "axios";

export default function CapturePage() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [steps, setSteps] = useState(0);
  const [countdown, setCountdown] = useState(null);
  const [background, setBackground] = useState({ type: "color", value: "#ff0000" });
  const [eventName, setEventName] = useState("");
  const [logo, setLogo] = useState(null);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) videoRef.current.srcObject = stream;
      })
      .catch(() => alert("Camera is not available. Please allow webcam access."));
  }, []);

  const startCapture = () => {
    if (steps >= 3) return;
    setCountdown(3);
    const countdownInterval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          takePhoto();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const takePhoto = () => {
    const ctx = canvasRef.current.getContext("2d");
    const width = 480;
    const height = 240;

    if (steps === 0) {
      // Set background
      if (background.type === "color") {
        ctx.fillStyle = background.value;
        ctx.fillRect(0, 0, width, 720);
      }
    }

    ctx.drawImage(videoRef.current, 0, steps * height, width, height);

    // Optional logo overlay
    if (logo) {
      const img = new Image();
      img.src = URL.createObjectURL(logo);
      img.onload = () => {
        ctx.drawImage(img, 10, 10, 100, 50);
      };
    }

    // Optional event name text on last step
    if (steps === 2) {
      ctx.fillStyle = "#ffffff";
      ctx.font = "24px sans-serif";
      ctx.fillText(eventName, 10, 710);
    }

    setSteps(steps + 1);
  };

  const convertLogoToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(file);
    });
  };

  const submit = async () => {
    try {
      const dataUrl = canvasRef.current.toDataURL("image/jpeg", 0.8);

      let logoBase64 = "";
      if (logo) {
        logoBase64 = await convertLogoToBase64(logo);
      }

      await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/strips`, {
        image: dataUrl,
        eventName,
        background,
        logo: logoBase64,
      });

      alert("✅ Thank you! Your photo strip is sent for print.");
      setSteps(0);
      canvasRef.current.getContext("2d").clearRect(0, 0, 480, 720);
    } catch (error) {
      console.error("Upload failed:", error);
      alert("❌ Failed to upload. Please check server connection.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-blue-600 to-teal-500">
      <div className="container mx-auto px-3 sm:px-4 lg:px-6 py-4 sm:py-6 lg:py-8">
        {/* Header */}
        <div className="text-center mb-6 sm:mb-8 lg:mb-12">
          <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-2 drop-shadow-lg">
            📸 Strip Photobooth
          </h1>
          <p className="text-base sm:text-lg md:text-xl text-white/80 px-4">Create amazing photo strips instantly!</p>
        </div>

        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6 lg:gap-8">
            {/* Camera Section */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h3 className="text-2xl font-bold text-white mb-4 text-center">📹 Camera</h3>

              <div className="relative">
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  className="w-full rounded-xl shadow-2xl border-4 border-white/30"
                />

                {countdown && (
                  <div className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-xl">
                    <div className="text-8xl font-bold text-white animate-pulse drop-shadow-lg">
                      {countdown}
                    </div>
                  </div>
                )}

                {/* Progress indicator */}
                <div className="mt-4 flex justify-center space-x-2">
                  {[1, 2, 3].map((step) => (
                    <div
                      key={step}
                      className={`w-4 h-4 rounded-full ${
                        steps >= step ? 'bg-green-400' : 'bg-white/30'
                      } transition-colors duration-300`}
                    />
                  ))}
                </div>
              </div>

              <button
                onClick={startCapture}
                disabled={steps >= 3}
                className={`mt-6 w-full py-4 px-6 rounded-xl font-bold text-lg transition-all duration-300 ${
                  steps >= 3
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 text-white shadow-lg hover:shadow-xl transform hover:scale-105'
                }`}
              >
                {steps >= 3 ? '✅ All Photos Captured!' : `📸 Capture Photo ${steps + 1}`}
              </button>
            </div>

            {/* Settings Section */}
            <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
              <h3 className="text-2xl font-bold text-white mb-4 text-center">⚙️ Settings</h3>

              <div className="space-y-6">
                {/* Event Name */}
                <div>
                  <label className="block text-white font-semibold mb-2">🎉 Event Name</label>
                  <input
                    type="text"
                    placeholder="Enter event name..."
                    value={eventName}
                    onChange={(e) => setEventName(e.target.value)}
                    className="w-full p-3 rounded-xl border-2 border-white/30 bg-white/20 text-white placeholder-white/60 focus:border-white focus:outline-none transition-colors"
                  />
                </div>

                {/* Background Color */}
                <div>
                  <label className="block text-white font-semibold mb-2">🎨 Background Color</label>
                  <div className="flex items-center space-x-3">
                    <input
                      type="color"
                      value={background.value}
                      onChange={(e) => setBackground({ type: "color", value: e.target.value })}
                      className="w-16 h-12 rounded-lg border-2 border-white/30 cursor-pointer"
                    />
                    <span className="text-white font-mono bg-white/20 px-3 py-2 rounded-lg">
                      {background.value}
                    </span>
                  </div>
                </div>

                {/* Logo Upload */}
                <div>
                  <label className="block text-white font-semibold mb-2">🖼️ Logo (Optional)</label>
                  <div className="relative">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setLogo(e.target.files[0])}
                      className="w-full p-3 rounded-xl border-2 border-white/30 bg-white/20 text-white file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:bg-white/30 file:text-white hover:file:bg-white/40 transition-colors"
                    />
                  </div>
                  {logo && (
                    <p className="text-green-300 text-sm mt-2">✅ Logo selected: {logo.name}</p>
                  )}
                </div>
              </div>

              {/* Submit Button */}
              {steps === 3 && (
                <button
                  onClick={submit}
                  className="mt-6 w-full py-4 px-6 bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white font-bold text-lg rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300"
                >
                  🚀 Submit Strip
                </button>
              )}
            </div>
          </div>

          {/* Preview Canvas (hidden) */}
          <canvas
            ref={canvasRef}
            width="480"
            height="720"
            className="hidden"
          />
        </div>
      </div>
    </div>
  );
}
