import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

export default function CapturePage() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [steps, setSteps] = useState(0);
  const [countdown, setCountdown] = useState(null);
  const [background, setBackground] = useState({ type: "color", value: "#ff0000" });
  const [eventName, setEventName] = useState("");
  const [logo, setLogo] = useState(null);

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        if (videoRef.current) videoRef.current.srcObject = stream;
      })
      .catch(() => alert("Camera is not available. Please allow webcam access."));
  }, []);

  const startCapture = () => {
    if (steps >= 3) return;
    setCountdown(3);
    const countdownInterval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          takePhoto();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const takePhoto = () => {
    const ctx = canvasRef.current.getContext("2d");
    const width = 480;
    const height = 240;

    if (steps === 0) {
      // Set background
      if (background.type === "color") {
        ctx.fillStyle = background.value;
        ctx.fillRect(0, 0, width, 720);
      }
    }

    ctx.drawImage(videoRef.current, 0, steps * height, width, height);

    // Optional logo overlay
    if (logo) {
      const img = new Image();
      img.src = URL.createObjectURL(logo);
      img.onload = () => {
        ctx.drawImage(img, 10, 10, 100, 50);
      };
    }

    // Optional event name text on last step
    if (steps === 2) {
      ctx.fillStyle = "#ffffff";
      ctx.font = "24px sans-serif";
      ctx.fillText(eventName, 10, 710);
    }

    setSteps(steps + 1);
  };

  const submit = async () => {
    try {
      const dataUrl = canvasRef.current.toDataURL("image/jpeg", 0.8);

      await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/strips`, {
        image: dataUrl,
        eventName,
        background,
        logo: logo ? URL.createObjectURL(logo) : "",
      });

      alert("‚úÖ Thank you! Your photo strip is sent for print.");
      setSteps(0);
      canvasRef.current.getContext("2d").clearRect(0, 0, 480, 720);
    } catch (error) {
      console.error("Upload failed:", error);
      alert("‚ùå Failed to upload. Please check server connection.");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl mb-4">üì∏ Strip Photobooth</h2>

      <video ref={videoRef} autoPlay playsInline className="w-full max-w-sm rounded shadow" />

      {countdown && (
        <div className="text-4xl font-bold text-center mt-2">{countdown}</div>
      )}

      <div className="mt-3 space-y-2">
        <input
          type="text"
          placeholder="Event Name"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          className="border p-1 w-full"
        />

        <input
          type="color"
          value={background.value}
          onChange={(e) => setBackground({ type: "color", value: e.target.value })}
          className="w-full"
        />

        <input
          type="file"
          accept="image/*"
          onChange={(e) => setLogo(e.target.files[0])}
          className="w-full"
        />
      </div>

      <button
        onClick={startCapture}
        disabled={steps >= 3}
        className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
      >
        Capture Photo {steps < 3 ? steps + 1 : ""}
      </button>

      {steps === 3 && (
        <button
          onClick={submit}
          className="mt-2 bg-green-600 text-white px-4 py-2 rounded block w-full"
        >
          Submit Strip
        </button>
      )}

      <canvas
        ref={canvasRef}
        width="480"
        height="720"
        className="hidden"
      />
    </div>
  );
}
