import React, { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [auth, setAuth] = useState(false);
  const [strips, setStrips] = useState([]);

  const login = async () => {
    try {
      console.log('Attempting login...');
      const res = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/login`, { username: "admin", password: "secret" });
      console.log('Login response:', res.data);
      if (res.data.success) {
        setAuth(true);
        console.log('Login successful, auth set to true');
      }
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  const load = async () => {
    try {
      console.log('Loading strips...');
      const res = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/strips`);
      console.log('Strips loaded:', res.data);
      setStrips(res.data);
    } catch (error) {
      console.error('Failed to load strips:', error);
    }
  };

  const mark = async (id) => {
    await axios.patch(`${process.env.REACT_APP_API_BASE_URL}/api/strips/${id}/mark-printed`);
    load();
  };

  const del = async (id) => {
    await axios.delete(`${process.env.REACT_APP_API_BASE_URL}/api/strips/${id}`);
    load();
  };

  useEffect(()=>{ if(auth) load(); }, [auth]);

  if (!auth) return <button onClick={login} className="mt-4">Admin Login</button>;

  return (
    <div className="p-4">
      <h2 className="text-xl mb-4">üõ† Admin Dashboard</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {strips.map(s=>(
          <div key={s._id} className="border p-2">
            <img src={s.imageUrl} alt="" className="w-full"/>
            <p>{new Date(s.timestamp).toLocaleString()}</p>
            <p>{s.eventName}</p>
            <p>Printed: {s.printed ? "‚úÖ" : "‚ùå"}</p>
            <button onClick={()=>mark(s._id)} className="mt-2 bg-yellow-400 px-2">Mark Printed</button>
            <button onClick={()=>del(s._id)} className="mt-2 bg-red-500 px-2 text-white">Delete</button>
            <button onClick={()=>window.open(s.imageUrl)} className="mt-2 bg-blue-500 px-2 text-white">Download</button>
            <button onClick={()=>window.print()} className="mt-2 bg-green-600 px-2 text-white">Print</button>
          </div>
        ))}
      </div>
    </div>
  );
}
